  return (
    <div className="a" style={{ backgroundColor: !darkMode && "#C9CCD5" }}>
      <div className="a-left">
        <div
          className="a-card bg"
          style={{
            backgroundColor: darkMode ? "#777" : "#333",
          }}
        ></div>
        <div className="a-card">
          <img src={Me} alt="" className="a-img" />
        </div>
      </div>
      <div className="a-right">
        <h1 className="a-title">About Me</h1>
        <div>
          <p className="a-sub">Hi, I'm Nathapot.</p>

          <p className="a-desc">
            I'm currently an Engineer student at King Mongkut's Institute of
            Technology Ladkrabang. Nowadays, technology is growing rapidly. I
            want to develop a modern Software for human.
          </p>
        </div>

        {/* {aboutMe ? (
          <div>
            <p className="a-sub">{aboutMe.title}</p>

            <p className="a-desc">{aboutMe.body}</p>
          </div>
        ) : (
          <SkeletonArticle />
        )} */}

        <div className="a-award">
          <img src={Award} alt="" className="a-award-img" />
          <div className="a-award-texts">
            <h4 className="a-award-title">Experience</h4>
            {/* <p className="a-award-desc">Desc Desc Desc Desc</p> */}
            <ul>
              <li className="a-award-desc">
                Data scientist (Intern) At Botnoi Consulting Co.,Ltd.
              </li>
              <li className="a-award-desc">
                Full Stack Developer (Part-time) At CTM
              </li>
              <li className="a-award-desc">
                The Development of Cancer Tracking System from Respiratory
                Motion using Deep Learning At Siriraj Hospital
              </li>
            </ul>
          </div>
        </div>

        {/* {project ? (
          <div className="a-award">
            <img src={Award} alt="" className="a-award-img" />
            <div className="a-award-texts">
              <h4 className="a-award-title">Test Test</h4>
              <p className="a-award-desc">Desc Desc Desc Desc</p>
            </div>
          </div>
        ) : (
          <SkeletonProject />
        )} */}
      </div>
    </div>
  );
};

export default About;
