import "./login.css";
import React, { useState } from "react";
import { Redirect } from "react-router-dom";
import axios from "axios";

const Login = ({ setName }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [redirect, setRedirect] = useState(false);

  // const submit = async (e) => {
  //   e.preventDefault();

  //   await fetch("http://localhost:8000/api/login", {
  //     method: "POST",
  //     headers: { "Content-Type": "application/json" },
  //     credentials: "include",
  //     body: JSON.stringify({
  //       email,
  //       password,
  //     }),
  //   });

  //   const response = await fetch("http://localhost:8000/api/user", {
  //     headers: { "Content-Type": "application/json" },
  //     credentials: "include",
  //   });

  //   const content = await response.json();
  //   setName(content.name);

  //   setRedirect(true);
  // };

  const headers = { "Content-Type": "application/json" };
  const credentials = "include";
  const body = JSON.stringify({
    email,
    password,
  });
  const submit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:8000/api/login", body, { headers }, credentials)
      .then((response) =>
        axios.get("http://localhost:8000/api/user", { headers }, credentials)
      )
      .then((content = response.json()))
      .then(console.log(content.name));
  };

  if (redirect) {
    return <Redirect to="/" />;
  }

  return (
    <form onSubmit={submit} className="form-login">
      <h1 className="h3 mb-3 fw-normal">Please login</h1>

      <input
        type="email"
        className="form-control"
        placeholder="name@example.com"
        required
        onChange={(e) => setEmail(e.target.value)}
      />

      <input
        type="password"
        className="form-control"
        placeholder="Password"
        required
        onChange={(e) => setPassword(e.target.value)}
      />

      <button className="w-100 btn btn-lg btn-primary" type="submit">
        Login
      </button>
    </form>
  );
};

export default Login;
