import "./about.css";
// import Me from "../../img/Potto.JPG";
import Award from "../../img/Project_GPR.jpg";
import React, { useEffect, useState } from "react";
import SkeletonArticle from "../skeletons/skeletonArticle";
import SkeletonProject from "../skeletons/skeletonProject";

const About = () => {
  const [iconProfile, setIconProfile] = useState(null);
  const [aboutMe, setAboutMe] = useState(null);
  const [project, setProject] = useState(null);

  useEffect(() => {
    setTimeout(async () => {
      const res = await fetch("https://jsonplaceholder.typicode.com/photos/1");
      const data = await res.json();
      setIconProfile(data);
    }, 3000);
  });

  useEffect(() => {
    setTimeout(async () => {
      const res = await fetch("https://jsonplaceholder.typicode.com/photos/1");
      const aboutMe = await res.json();
      setAboutMe(aboutMe);
    }, 3000);
  });

  useEffect(() => {
    setTimeout(async () => {
      const res = await fetch("https://jsonplaceholder.typicode.com/photos/1");
      const project = await res.json();
      setProject(project);
    }, 3000);
  });

  return (
    <div className="a">
      <div className="a-left">
        <div className="a-card bg"></div>
        <div className="a-card">
          {iconProfile && (
            <img src={iconProfile.url} alt="" className="a-img" />
          )}
          {!iconProfile && <img src={Award} alt="" className="a-img" />}
        </div>
      </div>
      <div className="a-right">
        <h1 className="a-title">About Me</h1>
        {aboutMe && (
          <div>
            <p className="a-sub">
              It is a long established fact that a reader will be del readable
              content
            </p>

            <p className="a-desc">
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Phasellus
              vestibulum lorem et faucibus volutpat. Nunc nunc erat, lobortis et
              tortor quis, finibus blandit mauris. Praesent ac libero ornare leo
              mollis ultrices in at turpis. Curabitur in neque ullamcorper nunc
              eleifend semper. Nam vehicula tristique condimentum. Cras id
              ligula tempor, molestie justo nec, condimentum elit. Nam eget arcu
              at est aliquet vestibulum.
            </p>
          </div>
        )}
        {!aboutMe && [1].map((n) => <SkeletonArticle key={n} />)}

        {project && (
          <div className="a-award">
            <img src={project.url} alt="" className="a-award-img" />
            <div className="a-award-texts">
              <h4 className="a-award-title">International Design</h4>
              <p className="a-award-desc">
                Nulla facilisi. Aenean congue bibendum diam eu tristique. Morbi
                justo lectus, commodo eu facilisis porttitor, malesuada maximus
                nulla.
              </p>
            </div>
          </div>
        )}
        {!project && [1].map((n) => <SkeletonProject key={n} />)}
      </div>
    </div>
  );
};

export default About;
