import "./contact.css";
import Phone from "../../img/phone.png";
import Email from "../../img/email.png";
import Address from "../../img/address.png";
import { useRef, useState } from "react";
import emailjs from "emailjs-com";

const Contact = () => {
  const formRef = useRef();
  const [done, setDone] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();
    emailjs
      .sendForm(
        "service_lfu0nl4",
        "template_8pc0vgr",
        formRef.current,
        "user_uHPKDLtD0TS1uVjEcxv4r"
      )
      .then(
        (result) => {
          console.log(result.text);
          setDone(true);
        },
        (error) => {
          console.log(error.text);
        }
      );
  };
  return (
    <div className="c">
      <div className="c-bg"></div>
      <div className="c-wrapper">
        <div className="c-left">
          <h1 className="c-title">Let's discuss your project</h1>
          <div className="c-info">
            <div className="c-info-item">
              <img src={Phone} alt="" className="c-icon" />
              085 055 7253
            </div>
            <div className="c-info-item">
              <img src={Email} alt="" className="c-icon" />
              Nathapot.po@gmail.com
            </div>
            <div className="c-info-item">
              <img src={Address} alt="" className="c-icon" />
              194/2 Nonsi Rd. Chongnonsi Yannawa Bangkok 10120
            </div>
          </div>
        </div>
        <div className="c-right">
          <p className="c-desc">
            <b>What's your story?</b>
            Get in touch. Always freelancing if the right project come along.
          </p>
          <form ref={formRef} onSubmit={sendEmail}>
            <input
              type="text"
              placeholder="Your name"
              name="user_name"
              required
            />
            <input
              type="text"
              placeholder="Subject"
              name="user_subject"
              required
            />
            <input
              type="email"
              placeholder="Your email"
              name="user_email"
              required
            />
            <textarea
              rows="5"
              placeholder="Message"
              name="message"
              required
            ></textarea>
            <button>Submit</button>
            {done ? <div>Successful!</div> : <div>Error has been occured</div>}
          </form>
        </div>
      </div>
    </div>
  );
};

export default Contact;
